    private StorageResource getAllGenes(String dna){
        StorageResource sr = new StorageResource();
        int startIndex = 0;
        System.out.println("entered getAllGenes");
        while(true){
            System.out.println("entered getAllGenes while loop");
            String currentGene = findGene(dna, startIndex);
            if (currentGene.isEmpty()) {
                System.out.println("entered getAllGenes break");
                break;
            }
            sr.add(currentGene);
            startIndex = startIndex + currentGene.length();
        }
        System.out.println("entered getAllGenes return");
        return sr;
    }
    
    public double cgRatio(String dna){
        int counter = 0;
        for (Character c : dna.toCharArray()) {
            if ((c == 'C') || (c == 'G')) {
                counter++;
            }
        }
        double output = (double) counter / dna.length();
        System.out.println("cgRatio = " + output);
        return output;
    }
    
    public void testQuizAnswers(){
        String dna = "";
        StorageResource sr = getAllGenes(dna);
        int counter = 0;
        int lengthCounter = 0;
        for (String s : sr.data()) {
            if (cgRatio(s) > 0.35){
                counter++;
            }
            if (s.length() > 60){
                lengthCounter++;
            }
        }
        System.out.println(counter); 
        System.out.println(lengthCounter);         
    }
    
    private int countCTG(String dna){
        int startIndex = 0;
        int counter = 0;
        while(true) {
            int indexOfCTG = dna.indexOf("CTG");
            if (indexOfCTG == -1) break;
            counter++;
            dna = dna.substring(indexOfCTG+3);
        }
        return counter;
    }

    private void processGenes(StorageResource sr){
        String longestGene = "";
        for (String string : sr.data()){
            System.out.println("Current dna sample = " + string);
            StorageResource genes = getAllGenes(string);
            int counterLength9 = 0;
            int counterCG = 0;
            for (String gene : genes.data()) {
                System.out.println("processGene genes = " + gene);
                if (gene.length() > 60) {
                    System.out.println("Length over 60: " + gene);
                    counterLength9++;
                }
                if (cgRatio(gene) > 0.35) {
                    System.out.println("CGRatio > 0.35 : " + gene);
                    counterCG++;
                }
                if (gene.length() > longestGene.length()) longestGene = gene;
            }
            System.out.println("number of genes longer than 9 chars = " + counterLength9);
            System.out.println("number of genes in gene with cgRation > 0.35 = " + counterCG);
            System.out.println("longest gene of entire storage resource so far= " + longestGene);
            System.out.println("longest gene length = " + longestGene.length());
        }
    }
    
