    private String findGene(String dna, int startingIndex) {
        System.out.println("entered findGene");
        dna = dna.toUpperCase();
        int indexOfATG = dna.indexOf("ATG", startingIndex);
        if (indexOfATG == -1) {
            System.out.println("entered findGene -1 ATG index");
            return "";
        }
        int indexOfTAA = findStopCodon(dna, indexOfATG, "TAA");
        int indexOfTAG = findStopCodon(dna, indexOfATG, "TAG");
        int indexOfTGA = findStopCodon(dna, indexOfATG, "TGA");
        int minIndex;
        if (indexOfTAA == -1 || (indexOfTAG != -1 && indexOfTAG < indexOfTAA)) 
            minIndex = indexOfTAG;
            else minIndex = indexOfTAA;
        if (minIndex == -1 || (indexOfTGA != -1 && indexOfTGA < minIndex))
            minIndex = indexOfTGA;
        if (minIndex == -1) {
            System.out.println("entered findGene minIndex == -1 return");
            return "";
        }
        System.out.println("entered bottom findGene return");
        return dna.substring(indexOfATG, minIndex+3);
    }
    
    private int findStopCodon(String dna, int startIndex, String stopCodon){
        System.out.println("entered findStopCodon");
        int currentIndex = dna.indexOf(stopCodon, startIndex+3);
        while (currentIndex != -1) {
            if ((currentIndex - startIndex) % 3 == 0) return currentIndex;
                currentIndex = dna.indexOf(stopCodon, currentIndex+1);
        }
        System.out.println("entered fondStopCodon -1 return");
        return -1;
    }
    

    
    
